var link=document.querySelector(".button-writeus"),popup=document.querySelector(".modal-writeus"),close=popup.querySelector(".modal-close"),username=popup.querySelector("[name=username]"),email=popup.querySelector("[name=email]"),storage=localStorage.getItem("username");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),storage?(username.value=storage,email.focus()):username.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&popup.classList.remove("modal-show")});